// Види селекторів для jquery
$('body')      // тегу 
$('.inputTime') // класу
$('#01')   // ідентифікатору
$('nav menu') // вставний вид селектора
$('.inputTime > div') // всі дочірні елементи класу input, тобто всі дочірні div
$('.inputTime img + h4') //сусідні елементи
$('img[height=150]') //селектори по атрибутах
$('img[src^=img/]') //селектор, який вибирає всі атрибути IMG які мають на початку назви 'img' 
$('img[src$=.icon]') //селектора, який вибирає всі атрибути IMG які мають закінчення 'icon'
$('a[href*=#]') //вибирає всі атрибути A, та шукає href із '#' у будь-якому місті href

//фільтрація вибірки
$('menu li:even') //вибирає всі парні елементи 'LI' !!!!! розрахунок елементів починається з 0
$('menu li:odd') // вибирає всі непарні елементи 'LI'
$('img:not(.logo img)') // вибирає всі елементи img крім конкретно вказаного
$('li:has(a)') // вибирає всі теги LI тільки якщо він має у собі тег А
$('p:contains(client)') // вибери всі Р де є слово CLIENT
$('li:first') // вибире перший тег LI
$('li:last') // вибире останній тег LI
// всі вище подані селектори можна комбінувати між собою наприклад
$('.logo li:first')
$('#h4 > div + li:last')


